    static func getSpectrum(integrationTimeMS: Int, gain: Int, laserEnabled: Bool) -> [Double]
    {
        let gainF = Float(gain) / 3.0
        
        let scalar = Double(50.0 * Float(integrationTimeMS) * gainF)
        let noise  = Double( 2.0 * Float(integrationTimeMS) * gainF)
        let offset = Double( 0.2 * Float(integrationTimeMS) * gainF * gainF)

        var spectrum = [Double]()
        for i in 0 ..< cyclohexane.count
        {
            let rand   = Double.random(in: 0 ..< 1)
            let signal = laserEnabled ? Double(scalar) * cyclohexane[i] : 0
            let counts = max(0, min(65535, offset + signal + noise / 2.0 - noise * rand))
            spectrum.append(counts)
        }
        return spectrum
    }
}
